import React, { useState } from 'react';

function Celda({ id, value, addScore, view, onGameOver }) {

    const [ver, setView] = useState(view); // Se inicializa con el valor recibido por props

    const handleClick = () => {
        if (!ver) {
            setView(true);  // Muestra el valor cuando se hace clic
            if (value === "") {
                addScore(-1, id); // Penaliza por muerte
                alert("GAME OVER");
                onGameOver(); // Llama la funci贸n de fin del juego para ocultar todas las celdas
            } else {
                addScore(value, id); // Suma el valor
            }
        }
    };

    return (
        <div style={{ width: 50, height: 50 }}>
            <button className="cell" id={id} onClick={handleClick}>
                {ver ? value : " "}  {/* Si 'ver' es true, muestra el valor, si es false, muestra " " */}
            </button>
        </div>
    );
}

export default Celda;
