import React, { useState } from 'react';

function Celda({ value }) {

    // Usamos useState para manejar si la celda ha sido "revelada" o no.
    const [view, setView] = useState(false);

    let valor = " "; // Sin clickear
    if (view) {
        valor = value; // Si est치 vista, mostrar el valor de la celda
        if (value === "游눢") {
            alert("Game Over"); // Si es una bomb            location.reload(); // Recargar la p치gina

        }
    }

    return (
        <div>
            <button
                className="cell"
                onClick={() => setView(true)} // Cambiar el estado al hacer clic
            >
                {/* Contenido de la celda */}
                {valor}
            </button>
        </div>
    );
}

export default Celda;
