import React, { useState } from 'react';
import Timer from './Timer';  // Suponemos que Timer es el componente que creaste

const ParentComponent = () => {
    const [MaxTime, setMaxTime] = useState(60);  // El tiempo máximo inicial del temporizador
    const [Restart, setRestart] = useState(false);  // Estado para controlar el reinicio del temporizador

    // Función para jugar y reiniciar el temporizador
    const btnPlayGame = () => {
        setMaxTime(60);  // Asegúrate de que el tiempo se establezca a 60 segundos
        setRestart(true); // Activa el reinicio del temporizador
    };

    // Resetea el estado de Restart a false después de un breve retraso
    useEffect(() => {
        if (Restart) {
            const timer = setTimeout(() => {
                setRestart(false); // Vuelve a false después de que el temporizador se reinicie
            }, 100); // Pequeño retraso para evitar cambios continuos
            return () => clearTimeout(timer); // Limpia el timeout si se desmonta
        }
    }, [Restart]); // Ejecuta este useEffect cuando `Restart` cambie

    return (
        <div>
            <Timer MaxTime={MaxTime} Restart={Restart} />
            <button onClick={btnPlayGame}>Jugar</button>
        </div>
    );
};

export default ParentComponent;
