import React, { useState } from 'react';

function Celda({ id, value, onGameOver, addScore }) {

    const [view, setView] = useState(false); //No se vera por default


    useEffect(() => {
        // Si el valor de resetView cambia a true, reseteamos el view
        if (resetView) {
            setView(false);
        }
    }, [value]); // Se ejecuta cada vez que resetView cambia

    function handleClick() { //Se clickea, se ve y agrega o quita el puntaje
        if (!view && value != " ") { // Solo ejecuta la lógica si no ha sido clickeada antes

            setView(true); //Revela el valor

            if (value === "💣") {
                addScore(-1); // Death
                alert("GAME OVER");

                // onGameOver(); // Llamar a la función para manejar el fin del juego
            } else {
                addScore(value); // + value
                console.log("Valor es " + value);
            }
        }
    };


    return (
        <div style={{ width: 50, height: 50 }}>
            <button className="cell" id={id}
                onClick={handleClick}
            >
                {view ? value : " "}
                {/* {Si view es true, muestra valor, si es false, muestra " "} */}
            </button>
        </div>
    );


