import React, { useState, useEffect } from 'react';

const Timer = ({ MaxTime, Restart }) => {
    const [seconds, setSeconds] = useState(MaxTime);

    // Reinicia el temporizador cuando Restart cambia a true
    useEffect(() => {
        if (Restart) {
            setSeconds(MaxTime); // Reinicia el tiempo
        }
    }, [Restart, MaxTime]); // Este useEffect se ejecuta cada vez que Restart o MaxTime cambian

    // Temporizador que cuenta cada segundo
    useEffect(() => {
        if (seconds <= 0) return; // Evita que siga contando en negativo

        const idTemporizador = setInterval(() => {
            setSeconds(prevSeconds => prevSeconds - 1); // Restar 1 segundo
        }, 1000);

        return () => clearInterval(idTemporizador); // Limpia el temporizador antes de crear uno nuevo
    }, [seconds]); // Este useEffect se ejecuta cada vez que `seconds` cambia

    return (
        <div className='lcdText text-danger pe-2 m-2 borderInsideS'>
            {seconds + " seg"}
        </div>
    );
};

export default Timer;
