import React, { useState, useEffect } from 'react';

function Celda({ id, value, onGameOver, addScore, view }) {

    const [ver, setView] = useState(false); // No se verá por defecto

    useEffect(() => {
        console.log("view cambio");
        setView(view);
    }, [view]); // Este useEffect se ejecuta cuando 'view' cambia



    function handleClick() { // Se clickea, se ve y agrega o quita el puntaje
        console.log("view: " + ver);
        if (!ver) {
            view = true;
            setView(true);  // Actualiza 'view' a true

            if (value === "💣") {
                addScore(-1, id); // Death
                alert("GAME OVER");

                // onGameOver(); // Llamar a la función para manejar el fin del juego
            } else {
                addScore(value, id); // + value
                console.log("Valor es " + value);
            }
        }
    };

    return (
        <div style={{ width: 50, height: 50 }}>
            <button className="cell" id={id} onClick={handleClick}>
                {ver ? value : " "}
                {/* Si ver es true, muestra valor, si es false, muestra " "} */}
            </button>
        </div>
    );
}

export default Celda;
