            "1", "ðŸ’£", "1", "0", "1", "ðŸ’£", "2", "1", "0", "0",
            "1", "2", "1", "0", "1", "2", "ðŸ’£", "1", "0", "0",
            "0", "1", "ðŸ’£", "1", "1", "2", "ðŸ’£", "1", "0", "0"
        ],
        2: [
            "1", "ðŸ’£", "1", "0", "1", "2", "2", "1", "0", "0",
            "1", "2", "2", "1", "1", "ðŸ’£", "ðŸ’£", "1", "0", "0",
            "0", "1", "ðŸ’£", "1", "0", "2", "3", "2", "1", "1",
            "1", "2", "2", "1", "0", "1", "ðŸ’£", "ðŸ’£", "2", "ðŸ’£",
