            "0", "1", "ðŸ’£", "1", "0", "0", "1", "ðŸ’£", "1", "0",
            "0", "1", "2", "2", "1", "1", "2", "2", "2", "1",
            "1", "2", "ðŸ’£", "1", "1", "ðŸ’£", "2", "ðŸ’£", "2", "1",
            "1", "ðŸ’£", "2", "1", "0", "1", "ðŸ’£", "2", "2", "2",
