import React, { useState, useEffect } from 'react';

const Timer = ({ MaxTime, Restart }) => {
    const [seconds, setSeconds] = useState(MaxTime);

    useEffect(() => {
        if (Restart == true) {
            setSeconds(MaxTime); // Reinicia el tiempo cuando Restart es true
        }

        if (seconds <= 0) return; // Evita que el temporizador siga contando después de 0

        // Inicia el temporizador
        const idTemporizador = setInterval(() => {
            setSeconds(prevSeconds => prevSeconds - 1); // prevSeconds => prevSeconds - 1 == setSeconds(function(prevSeconds) { return prevSeconds - 1;}

        }, 1000);

        // Limpia el temporizador al desmontar o actualizar
        return () => clearInterval(idTemporizador);

    }, [seconds, Restart]); // Corregido: Ahora tiene las dependencias correctas

    return (
        <div className='lcdText text-danger pe-2 m-2 borderInsideS'>
            {seconds + " seg"}
        </div>
    );
};

export default Timer;
