import React from 'react';
import Celda from './Celda';

function Tablero({ size }) { // Se recibe size como prop correctamente
    console.log("Size recibido en Tablero:", size); // Verifica que size se reciba correctamente
    const totalSize = size * size;

    return (
        <div className="bg-dark rounded-5 mt-5">
            <div className='bg-warning-subtle text-dark px-5 py-2 rounded-top-5'>
                <h2 className="text-center">Buscaminas</h2>
                <div className='row p-2 rounded-3'>
                    <h2 className='col-6 bg-light border border-black' id='time'>00:00</h2>
                    <h2 className='col-6 bg-light border border-black' id='score'>0</h2>
                </div>
            </div>

            {/* Contenedor del tablero donde se colocan las celdas */}
            <div
                className='card p-1 my-5 mx-5'
                id='board'
                style={{ display: 'grid', gridTemplateColumns: `repeat(${size}, 1fr)` }} // Se usa grid para hacer la cuadrícula
            >
                {/** 
                 * Se genera un array con `size * size` elementos para representar todas las celdas del tablero.
                 * `Array.from({ length: size * size })` crea un array con esa cantidad de elementos vacíos.
                 * `(_, index) => <Celda key={index} />` recorre cada elemento y renderiza un componente Celda.
                 */}

                {Array.from({ length: totalSize }, (_, index) => (
                    <Celda key={index} id={index} />
                ))}
            </div>

            <button className="btn btn-primary mb-4 mt-3 px-5">Jugar</button>
        </div>
    );
}

export default Tablero;
