import React, { useState } from 'react';
import Celda from './Celda';

function Tablero() {
    const size = 10; // Tamaño del tablero (10x10)
    const totalSize = size * size; // Número total de celdas

    // Generar 6 tableros distintos con minas en diferentes posiciones
    const generarTableros = () => {
        let tableros = [];
        for (let i = 0; i < 6; i++) {
            let minas = new Set();
            while (minas.size < 20) { // 20 minas por tablero
                minas.add(Math.floor(Math.random() * totalSize));
            }
            tableros.push(minas);
        }
        return tableros;
    };

    const tableros = generarTableros();
    const [tableroActual, setTableroActual] = useState(0);

    return (
        <div className="bg-dark rounded-5 mt-5">
            <div className='bg-warning-subtle text-dark px-5 py-2 rounded-top-5'>
                <h2 className="text-center">Buscaminas</h2>
                <div className='row p-2 rounded-3'>
                    <h2 className='col-6 bg-light border border-black' id='time'>00:00</h2>
                    <h2 className='col-6 bg-light border border-black' id='score'>0</h2>
                </div>
            </div>

            {/* Contenedor del tablero */}
            <div
                className='card p-1 my-5 mx-5'
                id='board'
                style={{ display: 'grid', gridTemplateColumns: `repeat(${size}, 1fr)` }}
            >
                {Array.from({ length: totalSize }, (_, index) => (
                    <Celda key={index} id={index} esMina={tableros[tableroActual].has(index)} />
                ))}
            </div>

            <button
                className="btn btn-primary mb-4 mt-3 px-5"
                onClick={() => setTableroActual((tableroActual + 1) % 6)}
            >
                Cambiar Tablero
            </button>
        </div>
    );
}

export default Tablero;
