import React, { useState, useEffect, use } from 'react';

function Celda({ id, value, onGameOver, addScore, view }) {

    const [ver, setView] = useState(false); //No se vera por default


    useEffect(() => {
        setView(view);

    }, [view]); // Este useEffect se ejecuta cuando Restart o MaxTime cambian


    function handleClick() { //Se clickea, se ve y agrega o quita el puntaje
        console.log("view: " + view);
        if (!view)
            view = true;

        if (!view && value != " ") { // Solo ejecuta la l贸gica si no ha sido clickeada antes
            view = true;
            // setView(true); //Revela el valor

            if (value === "") {
                addScore(-1, id); // Death
                alert("GAME OVER");

                // onGameOver(); // Llamar a la funci贸n para manejar el fin del juego
            } else {
                addScore(value, id); // + value
                console.log("Valor es " + value);
            }
        }
    };


    return (
        <div style={{ width: 50, height: 50 }}>
            <button className="cell" id={id}
                onClick={handleClick}
            >
                {ver ? value : " "}
                {/* {Si view es true, muestra valor, si es false, muestra " "} */}
            </button>
        </div>
    );


