import React, { useState, useEffect } from 'react';

const Timer = ({ MaxTime }) => {


    // Usamos useState para manejar el tiempo
    const [seconds, setSeconds] = useState(MaxTime);



    // useEffect para configurar el temporizador
    useEffect(() => {
        const intervalId = setInterval(() => {
            setSeconds(prevSeconds => prevSeconds - 1);
        }, 1000); // Se incrementa cada segundo

        // Limpiar el intervalo cuando el componente se desmonte
        return () => clearInterval(intervalId);



    }, []); // El array vacío significa que este efecto solo se ejecuta una vez cuando se monta el componente

    return (
        <div>
            {seconds}
        </div>
    );
};

export default Timer;
