import React, { use, useState } from 'react';
import Celda from './Celda';
import Timer from './Timer';

function Tablero() { // Se recibe size como prop correctamente

    const [MaxTime, setTime] = useState(0);
    const [Restart, setReset] = useState(false);

    const time = <Timer MaxTime={MaxTime} Restart={Restart} />;

    //generar pos de las bombas al azar en base al size posteriormente.
    const Tableros = {
        0: [
            " ", " ", " ", " ", " ", " ", " ", " ", " ", " ",
            " ", " ", " ", " ", " ", " ", " ", " ", " ", " ",
            " ", " ", " ", " ", " ", " ", " ", " ", " ", " ",
            " ", " ", " ", " ", " ", " ", " ", " ", " ", " ",
            " ", " ", " ", " ", " ", " ", " ", " ", " ", " ",
            " ", " ", " ", " ", " ", " ", " ", " ", " ", " ",
            " ", " ", " ", " ", " ", " ", " ", " ", " ", " ",
            " ", " ", " ", " ", " ", " ", " ", " ", " ", " ",
            " ", " ", " ", " ", " ", " ", " ", " ", " ", " ",
            " ", " ", " ", " ", " ", " ", " ", " ", " ", " "
        ],
        1: [
            "1", "1", "1", "0", "0", "1", "üí£", "1", "0", "0",
            "1", "üí£", "1", "0", "0", "1", "3", "3", "1", "0",
            "1", "3", "3", "1", "1", "1", "üí£", "üí£", "1", "0",
            "1", "üí£", "üí£", "1", "1", "1", "2", "3", "2", "1",
            "1", "2", "2", "1", "0", "0", "0", "1", "üí£", "1",
            "0", "0", "0", "0", "0", "1", "1", "2", "2", "1",
            "1", "1", "0", "0", "1", "2", "üí£", "1", "0", "0",
            "1", "üí£", "1", "0", "1", "üí£", "2", "1", "0", "0",
            "1", "2", "1", "0", "1", "2", "üí£", "1", "0", "0",
            "0", "1", "üí£", "1", "1", "2", "üí£", "1", "0", "0"
        ],
    };

    // const [mapaValores, setMapaValores] = useState(Array(25).fill(" "));
    // const celdas = mapaValores.map((item, index) =>
    //     <div className="col-auto p-0" key={index}><Celda valor={item} onCeldaClick={() => mostrarValor(index)} /></div>
    // );

    //Definir pro, valores componente
    const [MapValues, setMapValues] = useState(Array(100).fill(" "));
    const celdas = MapValues.map((value, index) =>
        <Celda key={index} value={value} id={index}
            addScore={AddScore} />); //Agrego la clase playing si se esta jugado


    // MapValues.map((value, index) =>
    // <Celda key={index} value={value} id={index}
    //     addScore={AddScore} />); //Agrego la clase playing si se esta jugado

    function RandomBoard() {
        return Math.floor(Math.random() * 3) + 1;
    }


    const btnPlayGame = () => {

        setMapValues(Tableros[0]);


        setTime(60);  // Reiniciar el tiempo a 60 segundos

        setReset(true);  // Establecer el estado a `true` para reiniciar el temporizador

        // Despu√©s de un ciclo de renderizado, establecemos `Restart` a `false` para permitir futuros reinicios
        setTimeout(() => {
            setReset(false);
        }, 0);  // Este setTimeout con 0 milisegundos asegura que el valor de `Restart` vuelva a `false` inmediatamente despu√©s del renderizado.


        console.log("timer: " + MaxTime);
        // console.log("Tablero seleccionado = " + newNumTab);
    };




    // Estado para manejar el puntaje, lo llamo desde celda
    const [score, setScore] = useState(0); //Inicializo el score en 0

    function AddScore(id) {
        const cellValue = Tableros[1][id]; // Obtiene el valor de la celda
        console.log("cell " + id + ": " + cellValue);

        if (cellValue === "üí£") {
            setScore(0); // Si es una bomba, reinicia el puntaje
            console.log("Game Over");
        } else {
            if (cellValue !== " ") {
                setScore(parseInt(score) + parseInt(cellValue));
                viewValue(id);
            }
        }
    }

    function viewValue(id) {
        const valoresNuevos = MapValues.slice();
        valoresNuevos[id] = Tableros[0][id];
        console.log(MapValues[id])
        setMapValues(valoresNuevos);

    }


    //Estado de game over

    // const [Played, setPlay] = useState(0);

    function GameOver() {
        setReset(true);  // Establecer el estado a `true` para reiniciar el temporizador
        setTime(0);
        setMapValues(Tableros[0]);

        // ResetBoard();
    }


    //Estado reset game

    // const [Reset, setReset] = useState();

    // function Reset() {
    //     btnPlayGame();
    //     const celdas = MapValues.map((value, index) =>
    //         <Celda key={index} value={value} id={index}
    //             addScore={AddScore} view={false} />);
    // }




    return (
        <div className="bg-dark rounded-5 mt-5">
            <div className='bg-warning-subtle text-dark px-5 py-2 rounded-top-5'>
                <h2 className="text-center">Buscaminas</h2>
                <div className='row p-2 rounded-3'>
                    <h2 className='col-6 bg-light border border-black ' id='time'>{time}</h2>
                    <h2 className='col-6 bg-light border border-black ' id='score'>{score + " pts"}</h2>
                </div>
            </div>

            {/* Contenedor del tablero donde se colocan las celdas */}
            <div
                className='card p-1 my-5 mx-5'
                id='board'>


                {celdas}
            </div>
            <div>
                <button className='btn btn-outline-secondary mt-2'
                    onClick={btnPlayGame}>
                    Jugar
                </button>
            </div>
        </div>
    );
}

export default Tablero;
