function Celda({ id, value, onGameOver }) {
    // Usamos useState para manejar si la celda ha sido "revelada" o no.
    const [view, setView] = useState(false);

    let valor = " "; // Sin clickear
    if (view) {
        valor = value; // Si está vista, mostrar el valor de la celda
        if (value === "💣") {
            // onGameOver(); // Llamar a la función para manejar el fin del juego
        }
        else {
            score += value;
        }
    }

    return (
        <div>
            <button
                className="cell"
                id={id}
                onClick={() => setView(true)} // Cambiar el estado al hacer clic
            >
                {/* Contenido de la celda */}
                {valor}
            </button>
        </div>
    );
}

export default Celda;
